SELECT TO_CHAR(alermseq) CODE, eventtype, content, sendid, friendid, starttime, endtime, variable1
FROM alerm
WHERE friendid = #{id}

SELECT count(*)
FROM alerm
WHERE sendid = #{id}
AND content = #{content}
AND friendid = #{friendid}
AND eventtype = #{type}

SELECT TO_CHAR(alermseq) CODE, eventtype, content, sendid, friendid, starttime, endtime, variable1
FROM alerm
WHERE alermseq= #{alermseq}
